import React, {useState, useEffect, useRef} from 'react';
import { HashRouter as Router, Routes, Route, Link, useParams, useNavigate, Navigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';

// Components split into separate files for clarity
import HeaderComponent from './components/Header';
import FooterComponent from './components/Footer';
import ModalComponent from './components/Modal';
import HomeComponent from './components/Home';
import BooksComponent from './components/Books';
import BookDetailComponent from './components/BookDetail';
import AdminComponent from './components/Admin';
import BookCard from './components/BookCard';
import Newsletter from './components/Newsletter';
import PartnerCard from './components/PartnerCard';
import StarRating from './components/StarRating';
import CartDrawer from './components/CartDrawer';

// -----------------------
// Sample data (replace with API)
// -----------------------
const SAMPLE_AUTHOR = {
  name: 'Author Name',
  tagline: 'Storyteller • Poet • Novelist',
  bio: `Award-winning author. Writes about memory, belonging, and small luminous moments.`,
  avatar: 'https://placehold.co/240x240?text=Author',
  social: { instagram: '#', goodreads: '#', twitter: '#' },
  stats: [
    { label: 'Books Published', value: 7 },
    { label: 'Awards Won', value: 3 },
    { label: 'Years Active', value: '12+' }, // FIXED: Changed 1:2 to '12+'
  ]
};

const SAMPLE_BOOKS = [
  {
    id: 'b1',
    title: 'First Light',
    subtitle: 'A collection of short stories',
    price: 249,
    // Changed 'cover' to 'images' array
    images: ['https://placehold.co/800x1200?text=First+Light', 'https://placehold.co/600x400?text=Interior+Shot+1', 'https://placehold.co/600x400?text=Back+Cover'],
    isbn: '978-1-23456-789-0',
    pubDate: '2024-11-01',
    pages: 208,
    genre: 'Fiction', // Category added
    description: `A luminous collection exploring beginnings and small human triumphs.`
  },
  {
    id: 'b2',
    title: 'Khyalon Ke Jharokhe',
    subtitle: 'Poetry by Mai Aghori',
    price: 199,
    images: ['https://placehold.co/800x1200?text=Khyalon+Ke+Jharokhe'],
    isbn: '978-1-11111-111-1',
    pubDate: '2025-02-14',
    pages: 120,
    genre: 'Poetry', // Category added
    description: `Be the bestseller recommendation. Poetic glimpses into everyday magic.`
  },
  {
    id: 'b3',
    title: 'Midnight Cartography',
    subtitle: 'A novel of maps and memory',
    price: 349,
    images: ['https://placehold.co/800x1200?text=Midnight+Cartography', 'https://placehold.co/600x400?text=Map+Detail'],
    isbn: '978-1-22222-222-2',
    pubDate: '2023-08-10',
    pages: 320,
    genre: 'Literary Fiction', // Category added
    description: `A story about charting inner landscapes and the people who remain on them.`
  },
  {
    id: 'b4',
    title: 'The Silent Observer',
    subtitle: 'A thriller of modern surveillance',
    price: 299,
    images: ['https://placehold.co/800x1200?text=Silent+Observer'],
    isbn: '978-1-33333-333-3',
    pubDate: '2024-05-20',
    pages: 380,
    genre: 'Thriller', // New Category
    description: `A gripping story that explores the paranoia of a society under constant watch.`
  }
];

const SAMPLE_REVIEWS = [
  { id: 1, rating: 5, user: 'Anya S.', date: 'Oct 15, 2024', comment: 'Absolutely brilliant. The prose is luminous and the characters felt incredibly real. A must-read for literary fiction lovers!' },
  { id: 2, rating: 4, user: 'Ravi M.', date: 'Oct 10, 2024', comment: 'A thoughtful collection. Some stories resonated deeply, others less so, but overall, a powerful debut.' },
  { id: 3, rating: 5, user: 'Priya K.', date: 'Sep 28, 2024', comment: 'I finished this in one sitting! The ending was unexpected and deeply satisfying. Highly recommend.' },
];

const SAMPLE_AUTHORS_COLLAB = [
  { name: 'Rohan Varma', book: 'City of Whispers', avatar: 'https://placehold.co/100x100?text=Rohan' },
  { name: 'Dr. Zoya Khan', book: 'Quantum Dreams', avatar: 'https://placehold.co/100x100?text=Zoya' },
];

const SAMPLE_SUPPORTERS = [
    { name: 'Lit Publishing', logo: 'https://placehold.co/150x50/E67D29/FFFFFF?text=Lit', website: '#', category: 'Publishing' },
    { name: 'The Reader Guild', logo: 'https://placehold.co/150x50/1E40AF/FFFFFF?text=Guild', website: '#', category: 'Community' },
    { name: 'Bookish Events Co.', logo: 'https://placehold.co/150x50/10B981/FFFFFF?text=Events', website: '#', category: 'Events' },
    { name: 'The Literary Review', logo: 'https://placehold.co/150x50/6D28D9/FFFFFF?text=Review', website: '#', category: 'Media' },
];

// NEW PARTNER DATA FOR HOME PAGE
const SAMPLE_PUBLISHERS = [
  { name: 'Aurora Books', image: 'https://placehold.co/100x100/A0522D/FFFFFF?text=AB', description: 'Leading publisher focusing on speculative fiction and new voices.', website: '#' },
  { name: 'Literary Press', image: 'https://placehold.co/100x100/4682B4/FFFFFF?text=LP', description: 'Dedicated to promoting excellence in regional Indian literature.', website: '#' },
];

const SAMPLE_TECH_PARTNERS = [
  { name: 'WordFlow Dev', image: 'https://placehold.co/100x100/008080/FFFFFF?text=WF', description: 'Developers specializing in seamless author and e-commerce platforms.', website: '#' },
  { name: 'Innovate Design', image: 'https://placehold.co/100x100/800080/FFFFFF?text=ID', description: 'UI/UX experts crafting beautiful and responsive reader experiences.', website: '#' },
];
// -----------------------
// Utilities
// -----------------------
function useLocalState(key, initial) {
  const [state, setState] = useState(() => {
    try { const s = localStorage.getItem(key); return s ? JSON.parse(s) : initial; } catch { return initial; }
  });
  useEffect(() => { try { localStorage.setItem(key, JSON.stringify(state)); } catch {} }, [key, state]);
  return [state, setState];
}

function currency(n){ return `₹${Number(n).toLocaleString('en-IN')}` }

// -----------------------
// UI: Custom Modal (replaces alert/confirm)
// -----------------------
function Modal({ modal, setModal }) {
  const { isOpen, title, message, onConfirm } = modal;
  if (!isOpen) return null;

  const isConfirmation = !!onConfirm;

  function handleAction(confirmed) {
    // Close the modal and reset state
    setModal({ isOpen: false, title: '', message: '', onConfirm: null });
    // If it's a confirmation and the user clicked 'Confirm', execute the callback
    if (confirmed && onConfirm) {
      onConfirm();
    }
  }

  return (
    <div className="fixed inset-0 bg-black/50 z-[100] flex items-center justify-center p-4">
      <motion.div
        initial={{ y: -50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ type: "spring", stiffness: 300, damping: 30 }}
        className="bg-white dark:bg-slate-700 p-6 rounded-lg shadow-2xl max-w-sm w-full"
      >
        <h3 className="font-serif text-xl mb-3">{title}</h3>
        <p className="text-slate-700 dark:text-slate-300 mb-6 text-sm">{message}</p>
        <div className="flex justify-end gap-3">
          {isConfirmation && (
            <button
              onClick={() => handleAction(false)}
              className="px-4 py-2 border border-slate-300 dark:border-slate-500 rounded-md text-sm text-slate-700 dark:text-slate-200 hover:bg-slate-100 dark:hover:bg-slate-600 transition"
            >
              Cancel
            </button>
          )}
          <button
            onClick={() => handleAction(true)}
            className={`px-4 py-2 rounded-md text-white font-medium text-sm transition ${isConfirmation ? 'bg-amber-500 hover:bg-amber-600' : 'bg-slate-900 hover:bg-slate-700'}`}
          >
            {isConfirmation ? 'Confirm' : 'OK'}
          </button>
        </div>
      </motion.div>
    </div>
  );
}

// -----------------------
// UI: Header with mobile nav and dark-accent style
// -----------------------
function Header({author, isAdmin}){
  const [open, setOpen] = useState(false);
  return (
    <header className="backdrop-blur bg-white/60 dark:bg-slate-900/60 sticky top-0 z-50 border-b border-slate-200 dark:border-slate-700">
      <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">
        <Link to="/" className="flex items-center gap-3" onClick={()=>setOpen(false)}>
          <img src={author.avatar} alt={`${author.name} avatar`} className="w-12 h-12 rounded-full object-cover shadow-sm" />
          <div>
            <h1 className="font-serif text-lg md:text-xl leading-none">{author.name}</h1>
            <p className="text-xs text-slate-600 dark:text-slate-300">{author.tagline}</p>
          </div>
        </Link>

        <nav className="hidden md:flex items-center gap-6">
          <NavLinks isAdmin={isAdmin} />
        </nav>

        {/* Mobile menu toggle */}
        <div className="md:hidden flex items-center">
          <button aria-label="menu" onClick={()=>setOpen(v=>!v)} className="p-2 rounded-md hover:bg-slate-100 dark:hover:bg-slate-700 transition">
            <svg className="w-6 h-6" viewBox="0 0 24 24" fill="none" stroke="currentColor"><path strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" d={open? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'} /></svg>
          </button>
        </div>
      </div>

      <AnimatePresence>
        {open && (
          <motion.div initial={{height:0,opacity:0}} animate={{height:'auto',opacity:1}} exit={{height:0,opacity:0}} className="md:hidden border-t border-slate-200 dark:border-slate-700">
            <div className="px-6 py-4 flex flex-col gap-3">
              <NavLinks mobile isAdmin={isAdmin} />
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </header>
  );
}

function NavLinks({mobile, isAdmin}){
  const base = "text-sm hover:underline font-medium";
  return (
    <>
      <Link to="/books" className={base + (mobile? ' block':'')}>Books</Link>
      {/* REMOVED Partners link as per user request */}
      <Link to="/about" className={base + (mobile? ' block':'')}>About</Link>
      <Link to="/contact" className={base + (mobile? ' block':'')}>Contact</Link>
      {/* Conditionally render Admin link */}
      {isAdmin ? (
        <Link to="/admin" className={(mobile? 'block ':'') + 'px-3 py-2 bg-slate-900 hover:bg-slate-700 text-white rounded-lg text-sm transition shadow'}>Admin</Link>
      ) : (
        <Link to="/admin/login" className={(mobile? 'block ':'') + 'px-3 py-2 border border-slate-300 dark:border-slate-600 rounded-lg text-sm transition hover:bg-slate-100 dark:hover:bg-slate-700'}>Login</Link>
      )}
    </>
  );
}

// -----------------------
// Footer
// -----------------------
function Footer({author}){
  return (
    <footer className="mt-16 bg-gradient-to-tr from-slate-900 to-slate-800 text-white rounded-t-xl shadow-inner">
      <div className="max-w-7xl mx-auto px-6 py-10 flex flex-col md:flex-row items-center justify-between gap-6">
        <div className="text-center md:text-left">
          <h3 className="font-serif text-xl">{author.name}</h3>
          <p className="text-sm text-slate-300">{author.tagline}</p>
        </div>
        <div className="text-sm text-slate-400">© {new Date().getFullYear()} {author.name}. All rights reserved.</div>
      </div>
    </footer>
  );
}

// -----------------------
// Publishers & Tech Partners Section
// -----------------------
function PublishersTechPartnersSection() {
  return (
    <section className="mt-12 pt-8 border-t border-slate-200 dark:border-slate-700">
      <h3 className="font-serif text-3xl mb-10 text-center text-amber-600 dark:text-amber-500">Publishing & Tech Partners</h3>

      {/* Publication Houses */}
      <h4 className="font-serif text-2xl mb-6 text-slate-700 dark:text-slate-300 border-b pb-2 border-dashed">Publishing Houses</h4>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 gap-8">
        {SAMPLE_PUBLISHERS.map((p, index) => (
          <PartnerCard key={`pub-${index}`} partner={p} />
        ))}
      </div>

      {/* Tech Developers */}
      <h4 className="font-serif text-2xl mt-12 mb-6 text-slate-700 dark:text-slate-300 border-b pb-2 border-dashed">Technology & Design Partners</h4>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 gap-8">
        {SAMPLE_TECH_PARTNERS.map((p, index) => (
          <PartnerCard key={`tech-${index}`} partner={p} />
        ))}
      </div>
    </section>
  );
}

// -----------------------
// UPDATED: Trusted Supporters Section
// -----------------------
function TrustedSupportersSection() {
    return (
        <section className="mt-16 pt-12 border-t border-slate-200 dark:border-slate-700 bg-slate-50 dark:bg-slate-900 rounded-xl p-6">
            <div className="text-center mb-8">
                <h3 className="font-serif text-3xl mb-2 text-amber-600 dark:text-amber-500">Our Trusted Supporters</h3>
                <p className="text-slate-600 dark:text-slate-400">Collaborating with the best in the literary and cultural space.</p>
            </div>

            <div className="grid grid-cols-2 sm:grid-cols-4 gap-6">
                {SAMPLE_SUPPORTERS.map((supporter, index) => (
                    <motion.a
                        key={index}
                        href={supporter.website}
                        target="_blank"
                        rel="noopener noreferrer"
                        initial={{ opacity: 0, scale: 0.9 }}
                        whileInView={{ opacity: 1, scale: 1 }}
                        whileHover={{ scale: 1.05, boxShadow: "0 10px 15px -3px rgba(245, 158, 11, 0.2)" }}
                        transition={{ duration: 0.4, delay: index * 0.05 }}
                        viewport={{ once: true }}
                        className="flex flex-col items-center justify-center p-4 bg-white dark:bg-slate-800 rounded-xl shadow-md border border-slate-200 dark:border-slate-700 group cursor-pointer transition"
                    >
                         <img
                            src={supporter.logo}
                            alt={supporter.name}
                            title={supporter.name}
                            className="h-12 w-auto filter grayscale opacity-70 group-hover:opacity-100 group-hover:grayscale-0 transition-all duration-300"
                            onError={(e) => {e.target.onerror = null; e.target.src='https://placehold.co/150x50?text=Logo';}}
                        />
                        <div className="mt-3 text-sm font-medium text-slate-800 dark:text-slate-200">{supporter.name}</div>
                        <div className="text-xs text-amber-500">{supporter.category}</div>
                    </motion.a>
                ))}
            </div>
        </section>
    );
}

// -----------------------
// Home / Hero (PartnersSection re-added)
// -----------------------
function Home({books, author, showAlert}){
  // Find the latest book to feature
  const featuredBook = books.sort((a, b) => new Date(b.pubDate) - new Date(a.pubDate))[0] || books[0];
  const featuredImage = featuredBook?.images?.[0] || 'https://placehold.co/800x1200?text=Book'; // Use first image

  return (
