import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../lib/api';

export default function BlogPage(){
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(()=>{
    let mounted = true;
    async function load(){
      setLoading(true);
      try{
        const b = await api.fetchBlog(id);
        if(mounted) setBlog(b);
      }catch(err){ if(mounted) setError(err.message || String(err)); }
      if(mounted) setLoading(false);
    }
    load();
    return ()=> mounted = false;
  }, [id]);

  if(loading) return <main className="max-w-4xl mx-auto px-6 py-12">Loading...</main>;
  if(error) return <main className="max-w-4xl mx-auto px-6 py-12">Error: {String(error)}</main>;
  if(!blog) return <main className="max-w-4xl mx-auto px-6 py-12">Post not found</main>;

  return (
    <main className="max-w-4xl mx-auto px-6 py-12">
      <h1 className="font-serif text-3xl mb-3 text-amber-600">{blog.title}</h1>
      {blog.image && <img src={blog.image} alt={blog.title} className="w-full h-64 object-cover rounded-lg mb-6" onError={(e)=>{e.target.onerror=null;e.target.src='https://placehold.co/800x450?text=Blog'}} />}
      <div className="prose dark:prose-invert max-w-none text-slate-700" dangerouslySetInnerHTML={{ __html: blog.content || `<p>${blog.excerpt || ''}</p>` }} />
      <div className="mt-8">
        <Link to="/blogs" className="text-amber-600 hover:underline">‚Üê Back to Blog</Link>
      </div>
    </main>
  );
}
