import React from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';

function currency(n){ return `â‚¹${Number(n).toLocaleString('en-IN')}` }

export default function BookCard({ book }){
  const navigate = useNavigate();
  const coverImage = book.images?.[0] || 'https://placehold.co/800x1200?text=Cover';

  return (
    <motion.article layout whileHover={{y:-6, boxShadow: "0 10px 15px -3px rgba(245, 158, 11, 0.1), 0 4px 6px -2px rgba(245, 158, 11, 0.05)"}} className="bg-white dark:bg-slate-800 rounded-2xl shadow-xl overflow-hidden flex flex-col transition duration-300 ease-in-out">
      <div className="relative">
        <img src={coverImage} alt={book.title} className="w-full h-72 object-cover" onError={(e) => {e.target.onerror = null; e.target.src='https://placehold.co/800x1200?text=Cover'}} />
        <div className="absolute left-4 bottom-4 bg-amber-500/90 dark:bg-amber-600/90 text-white rounded-full px-4 py-1 text-xs font-semibold shadow-md">
          {book.genre}
        </div>
      </div>
      <div className="p-5 flex-1 flex flex-col">
        <h4 className="font-serif text-xl">{book.title}</h4>
        <p className="text-sm text-slate-600 dark:text-slate-300 mt-1 flex-1">{book.subtitle}</p>

        <div className="mt-4 flex items-center justify-between pt-3 border-t border-dashed border-slate-200 dark:border-slate-700">
          <div>
            <div className="text-xl font-bold text-amber-600 dark:text-amber-500">{currency(book.price)}</div>
            <div className="text-xs text-slate-500">{book.pages} pages</div>
          </div>
          <div className="flex items-center gap-2">
            <button onClick={() => navigate(`/book/${book.id}`)} className="px-4 py-2 bg-slate-100 dark:bg-slate-700 rounded-lg font-medium text-sm hover:bg-slate-200 dark:hover:bg-slate-600 transition">View Details</button>
          </div>
        </div>
      </div>
    </motion.article>
  );
}
