import React from 'react';
import BlogCard from './BlogCard';

export default function BlogList({ blogs = [] }){
  if(!blogs || !blogs.length) return (
    <section className="max-w-5xl mx-auto px-6 py-12">
      <h3 className="font-serif text-2xl mb-4">Latest Posts</h3>
      <div className="text-slate-500">No blog posts yet.</div>
    </section>
  );

  return (
    <section className="max-w-7xl mx-auto px-6 py-12">
      <h3 className="font-serif text-2xl mb-6">Latest Posts</h3>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {blogs.map(b => (
          <BlogCard key={b._id || b.id} blog={b} />
        ))}
      </div>
    </section>
  );
}
