import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../lib/api';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import ShareButtons from './ShareButtons';

export default function BlogPage(){
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(()=>{
    let mounted = true;
    async function load(){
      setLoading(true);
      try{
        const b = await api.fetchBlog(id);
        if(mounted) setBlog(b);
      }catch(err){ if(mounted) setError(err.message || String(err)); }
      if(mounted) setLoading(false);
    }
    load();
    return ()=> mounted = false;
  }, [id]);

  if(loading) return <main className="max-w-4xl mx-auto px-6 py-12">Loading...</main>;
  if(error) return <main className="max-w-4xl mx-auto px-6 py-12">Error: {String(error)}</main>;
  if(!blog) return <main className="max-w-4xl mx-auto px-6 py-12">Post not found</main>;

  const isHtml = (s='') => /<\/?[a-z][\s\S]*>/i.test(s || '');
  const date = blog.publishedAt ? new Date(blog.publishedAt).toLocaleDateString() : null;

  return (
    <main className="max-w-5xl mx-auto px-6 py-12">
      <article className="bg-white dark:bg-slate-800 rounded-2xl shadow-lg overflow-hidden">
        {blog.image && (
          <div className="relative">
            <img src={blog.image} alt={blog.title} className="w-full h-80 object-cover" onError={(e)=>{e.target.onerror=null;e.target.src='https://placehold.co/1200x600?text=Blog'}} />
            <div className="absolute inset-0 bg-gradient-to-t from-black/50 via-transparent to-transparent" />
            <div className="absolute left-6 bottom-6 text-white">
              <div className="text-sm">{date}</div>
              <h1 className="font-serif text-4xl font-semibold leading-tight">{blog.title}</h1>
            </div>
          </div>
        )}

        <div className="p-8">
          <div className="flex items-start justify-between gap-6">
            <div className="flex-1">
              <div className="prose dark:prose-invert max-w-none text-slate-700">
                {isHtml(blog.content) ? (
                  <div dangerouslySetInnerHTML={{ __html: blog.content }} />
                ) : (
                  <ReactMarkdown remarkPlugins={[remarkGfm]}>{blog.content || blog.excerpt || ''}</ReactMarkdown>
                )}
              </div>

              <div className="mt-8 flex items-center justify-between">
                <div className="flex items-center gap-4">
                  {Array.isArray(blog.tags) && blog.tags.map((t,i)=> <span key={i} className="text-xs px-2 py-1 bg-slate-100 dark:bg-slate-700 rounded">{t}</span>)}
                </div>
                <ShareButtons url={window.location.href} title={blog.title} />
              </div>
            </div>
          </div>
        </div>
      </article>

      <div className="mt-6">
        <Link to="/blogs" className="text-amber-600 hover:underline">‚Üê Back to Blog</Link>
      </div>
    </main>
  );
}
