import React from 'react';
import { Link } from 'react-router-dom';

export default function BlogCard({ blog }){
  const image = blog.image || 'https://placehold.co/800x450?text=Blog';
  return (
    <article className="bg-white dark:bg-slate-800 rounded-xl shadow-md overflow-hidden">
      <Link to={`/blog/${blog._id || blog.id}`} className="block">
        <img src={image} alt={blog.title} className="w-full h-44 object-cover" onError={(e)=>{e.target.onerror=null; e.target.src='https://placehold.co/800x450?text=Blog'}} />
      </Link>
      <div className="p-4">
        <h4 className="font-serif text-lg mb-1"><Link to={`/blog/${blog._id || blog.id}`}>{blog.title}</Link></h4>
        <p className="text-sm text-slate-600 dark:text-slate-300 mb-3">{blog.excerpt}</p>
        <div className="text-right">
          <Link to={`/blog/${blog._id || blog.id}`} className="text-sm text-amber-600 hover:underline">Read more â†’</Link>
        </div>
      </div>
    </article>
  );
}
