          >
            Login
          </button>
        </form>
      </div>
    </motion.main>
  );
}


// -----------------------
// Admin Dashboard Component
// -----------------------
function Admin({books, setBooks, showAlert, logoutAdmin, author, setAuthor}){
  const navigate = useNavigate(); 
  const [editing, setEditing] = useState(null);

  // Get unique genres for suggestion purposes
  const existingGenres = [...new Set(books.map(b => b.genre).filter(g => g))].sort();

  // --- About Page / Author editor state ---
  const emptyAuthorForm = {
    name: '',
    tagline: '',
    bio: '',
    avatar: '',
    social: { instagram: '', goodreads: '', twitter: '' },
    stats: []
  };

  const [authorForm, setAuthorForm] = useState(author || emptyAuthorForm);

  useEffect(() => {
    // Sync local form with current author prop when it changes
    setAuthorForm(author || emptyAuthorForm);
  }, [author]);

  function handleSocialChange(key, value) {
    setAuthorForm(prev => ({ ...prev, social: { ...prev.social, [key]: value } }));
  }

  function handleStatChange(index, field, value) {
    const newStats = [...(authorForm.stats || [])];
    newStats[index] = { ...newStats[index], [field]: value };
    setAuthorForm(prev => ({ ...prev, stats: newStats }));
  }

  function addStat() {
    setAuthorForm(prev => ({ ...prev, stats: [ ...(prev.stats || []), { label: '', value: '' } ] }));
  }

  function removeStat(index) {
    setAuthorForm(prev => ({ ...prev, stats: prev.stats.filter((_, i) => i !== index) }));
  }

  function saveAuthor() {
    if (!authorForm.name || !authorForm.bio) return showAlert('Validation Error', 'Author Name and Bio are required.');
    // Ensure stats values exist and are strings
    const cleanedStats = (authorForm.stats || []).map(s => ({ label: (s.label||'').trim(), value: (s.value||'').toString().trim() })).filter(s => s.label || s.value);
    const newAuthor = { ...authorForm, stats: cleanedStats };
    setAuthor(newAuthor);
    showAlert('Saved', 'About page content updated successfully.');
  }

  const empty = {
    title:'', subtitle:'', price:'',
    images: ['https://placehold.co/800x1200?text=New+Book+Cover'], // Single default placeholder
    description:'', genre:'', pages:0, isbn:'', pubDate: new Date().toISOString().slice(0, 10)
  };
  const [form, setForm] = useState(empty);

  useEffect(()=>{
    if(editing) {
      // Ensure 'images' is an array when setting the form state from existing data
      setForm({...editing, images: editing.images || [empty.images[0]]});
    } else {
      setForm(empty);
    }
  }, [editing]);

  function handleImageChange(index, value) {
    const newImages = [...form.images];
    newImages[index] = value;
    // Only update the form state with the new array
    setForm({...form, images: newImages });
  }

  function addImage() {
    // Add an empty string for the new URL input
    setForm({...form, images: [...form.images, ''] });
  }

  function removeImage(index) {
    if (form.images.length === 1) {
      return showAlert('Error', 'A book must have at least one image (the primary cover).');
    }
    const newImages = form.images.filter((_, i) => i !== index);
    setForm({...form, images: newImages });
  }

  function save(){
    // Filter out empty/whitespace images before validation/saving
    const cleanedImages = form.images.filter(img => img && img.trim());

    // basic validation
    if(!form.title || !form.price || !form.genre) return showAlert('Validation Error', 'Book Title, Price, and Genre are required.');
    if(cleanedImages.length === 0) return showAlert('Validation Error', 'At least one cover image URL is required.');

    const newBook = {
      ...form,
      images: cleanedImages, // Use cleaned array
      price: Number(form.price),
      pages: Number(form.pages)
    };

    if(editing){
      setBooks(prev => prev.map(b => b.id === editing.id ? {...newBook, id: editing.id} : b));
      setEditing(null);
    }
    else {
      setBooks(prev => [{...newBook, id:`b${Date.now()}`}, ...prev]);
      setForm(empty);
    }
  }

  function remove(id){
    showAlert('Confirm Deletion', 'Are you sure you want to delete this book? This action cannot be undone.', () => {
        setBooks(prev => prev.filter(b=>b.id!==id));
    });
  }

  const previewImage = form.images?.[0] || 'https://placehold.co/400x600?text=Cover';

  return (
    <motion.main initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="max-w-7xl mx-auto px-6 py-12">
      <h2 className="font-serif text-3xl mb-6 text-amber-600 dark:text-amber-500">Admin Dashboard</h2>
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2">
          <h3 className="text-xl mb-4 font-semibold">Book List ({books.length})</h3>
          <div className="grid gap-4">
            {books.map(b=> (
              <div key={b.id} className="p-4 border border-slate-300 dark:border-slate-700 bg-white dark:bg-slate-800 rounded-xl flex items-center justify-between shadow-sm">
                <div className="flex items-center gap-4">
                  <img src={b.images?.[0]} alt={b.title} className="w-16 h-24 object-cover rounded-md shadow-md" onError={(e) => {e.target.onerror = null; e.target.src='https://placehold.co/400x600?text=Img'}}/>
                  <div>
                    <div className="font-semibold text-lg">{b.title}</div>
                    <div className="text-sm text-slate-500">{b.genre} • {currency(b.price)}</div>
                  </div>
                </div>
                <div className="flex gap-2">
                  <button onClick={()=>setEditing(b)} className="px-4 py-2 border border-amber-500 text-amber-600 dark:text-amber-500 rounded-lg hover:bg-amber-50 transition text-sm">Edit</button>
                  <button onClick={()=>remove(b.id)} className="px-4 py-2 border border-rose-500 text-rose-500 rounded-lg hover:bg-rose-50 transition text-sm">Delete</button>
                </div>
              </div>
            ))}
          </div>
        </div>

        <aside className="p-6 border border-slate-300 dark:border-slate-700 rounded-xl bg-white dark:bg-slate-800 shadow-lg h-fit sticky top-20">
          <h3 className="text-xl font-semibold mb-4">{editing? 'Edit Book' : 'Add New Book'}</h3>
          <div className="space-y-3">
            <input value={form.title} onChange={e=>setForm({...form, title:e.target.value})} placeholder="Title" className="p-3 border rounded-lg w-full dark:bg-slate-900" required />
            <input value={form.subtitle} onChange={e=>setForm({...form, subtitle:e.target.value})} placeholder="Subtitle" className="p-3 border rounded-lg w-full dark:bg-slate-900" />
            <input type="number" value={form.price} onChange={e=>setForm({...form, price:e.target.value})} placeholder="Price (₹)" className="p-3 border rounded-lg w-full dark:bg-slate-900" required />

            {/* Genre Input (Admin Update) */}
            <input 
                value={form.genre} 
                onChange={e=>setForm({...form, genre:e.target.value})} 
                placeholder="Genre (e.g., Fiction, Poetry, Thriller)" 
                className="p-3 border rounded-lg w-full dark:bg-slate-900" 
                list="genre-suggestions"
                required
            />
            <datalist id="genre-suggestions">
                {existingGenres.map(genre => <option key={genre} value={genre} />)}
            </datalist>

            <input type="number" value={form.pages} onChange={e=>setForm({...form, pages:e.target.value})} placeholder="Pages" className="p-3 border rounded-lg w-full dark:bg-slate-900" />
            <input value={form.isbn} onChange={e=>setForm({...form, isbn:e.target.value})} placeholder="ISBN" className="p-3 border rounded-lg w-full dark:bg-slate-900" />
            <input type="date" value={form.pubDate} onChange={e=>setForm({...form, pubDate:e.target.value})} placeholder="Publication Date" className="p-3 border rounded-lg w-full dark:bg-slate-900" />
            <textarea value={form.description} onChange={e=>setForm({...form, description:e.target.value})} placeholder="Description" className="p-3 border rounded-lg w-full h-32 dark:bg-slate-900" />

            {/* --- Multiple Image Input Section --- */}
            <h4 className="pt-3 font-semibold text-sm text-slate-500 dark:text-slate-400">Book Images (First is Cover)</h4>
            <div className="space-y-3">
              {form.images.map((image, index) => (
                <div key={index} className="flex gap-2">
                  <input
                    value={image}
                    onChange={e => handleImageChange(index, e.target.value)}
                    placeholder={index === 0 ? "Primary Cover URL (Required)" : `Image URL ${index + 1}`}
                    className="p-3 border rounded-lg w-full dark:bg-slate-900"
                    required={index === 0} // Primary cover is required
                  />
                  <button
                    onClick={() => removeImage(index)}
                    className={`px-3 py-1 rounded-lg transition text-white ${index === 0 ? 'bg-slate-400 cursor-not-allowed' : 'bg-rose-500 hover:bg-rose-700'}`}
                    disabled={index === 0}
                    type="button"
                    title={index === 0 ? "Cannot remove primary cover" : "Remove Image"}
                  >
                    ✕
                  </button>
                </div>
              ))}
              <button onClick={addImage} className="inline-flex items-center gap-2 px-3 py-2 bg-slate-900 hover:bg-slate-700 text-white rounded-lg transition" type="button">
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6"/></svg>
                Add Image
              </button>
            </div>
            {/* ------------------------------------- */}
          </div>
          <div className="flex gap-3 mt-4">
            <button onClick={save} className="px-5 py-2 bg-emerald-600 hover:bg-emerald-700 text-white rounded-lg font-semibold transition">Save</button>
            <button onClick={()=>{ setEditing(null); setForm(empty); }} className="px-5 py-2 border border-slate-400 dark:border-slate-600 rounded-lg hover:bg-slate-100 dark:hover:bg-slate-700 transition">
              {editing ? 'Cancel Edit' : 'Clear Form'}
            </button>
          </div>

          {/* Preview */}
          {form.title && (
            <div className="mt-6 pt-4 border-t border-dashed border-slate-300 dark:border-slate-700">
              <h5 className="font-semibold text-lg mb-3">Live Card Preview</h5>
              <div className="p-3 border border-slate-300 dark:border-slate-700 rounded-xl flex gap-4 items-center">
                <img src={previewImage} alt="preview" className="w-16 h-24 object-cover rounded-md shadow" onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/400x600?text=Cover Not Found'; }} />
                <div>
                  <div className="font-bold text-lg">{form.title}</div>
                  <div className="text-sm text-slate-500">{form.genre} • {form.pages || '??'} pages</div>
                  <div className="text-amber-600 font-semibold mt-1">{currency(form.price || 0)}</div>
                </div>
              </div>
            </div>
          )}

          {/* About Page Editor (Admin) */}
          <div className="mt-6 pt-4 border-t border-dashed border-slate-300 dark:border-slate-700">
            <h4 className="font-semibold mb-3">About Page Editor</h4>
            <div className="space-y-3">
              <input value={authorForm.name} onChange={e=>setAuthorForm({...authorForm, name:e.target.value})} placeholder="Author Name" className="p-3 border rounded-lg w-full dark:bg-slate-900" />
              <input value={authorForm.tagline} onChange={e=>setAuthorForm({...authorForm, tagline:e.target.value})} placeholder="Tagline" className="p-3 border rounded-lg w-full dark:bg-slate-900" />
              <input value={authorForm.avatar} onChange={e=>setAuthorForm({...authorForm, avatar:e.target.value})} placeholder="Avatar URL" className="p-3 border rounded-lg w-full dark:bg-slate-900" />
              <textarea value={authorForm.bio} onChange={e=>setAuthorForm({...authorForm, bio:e.target.value})} placeholder="Short Bio" className="p-3 border rounded-lg w-full h-28 dark:bg-slate-900" />

              <div>
                <div className="text-sm text-slate-500 mb-2">Social Links</div>
                <input value={authorForm.social?.instagram || ''} onChange={e=>handleSocialChange('instagram', e.target.value)} placeholder="Instagram URL" className="p-2 border rounded-lg w-full dark:bg-slate-900 mb-2" />
                <input value={authorForm.social?.goodreads || ''} onChange={e=>handleSocialChange('goodreads', e.target.value)} placeholder="Goodreads URL" className="p-2 border rounded-lg w-full dark:bg-slate-900 mb-2" />
                <input value={authorForm.social?.twitter || ''} onChange={e=>handleSocialChange('twitter', e.target.value)} placeholder="Twitter URL" className="p-2 border rounded-lg w-full dark:bg-slate-900" />
              </div>

              <div>
                <div className="flex items-center justify-between mb-2">
                  <div className="text-sm text-slate-500">Milestones / Stats</div>
                  <button onClick={addStat} type="button" className="px-2 py-1 bg-slate-900 text-white rounded text-sm">Add</button>
                </div>
                <div className="space-y-2">
                  {(authorForm.stats || []).map((s, idx) => (
                    <div key={idx} className="flex gap-2">
                      <input value={s.label} onChange={e=>handleStatChange(idx, 'label', e.target.value)} placeholder="Label (e.g., Books Published)" className="p-2 border rounded-lg w-1/2 dark:bg-slate-900" />
                      <input value={s.value} onChange={e=>handleStatChange(idx, 'value', e.target.value)} placeholder="Value (e.g., 7)" className="p-2 border rounded-lg w-1/3 dark:bg-slate-900" />
                      <button onClick={()=>removeStat(idx)} type="button" className="px-2 py-1 bg-rose-500 text-white rounded">✕</button>
                    </div>
                  ))}
                </div>
              </div>

              <div className="flex gap-2">
                <button onClick={saveAuthor} className="px-4 py-2 bg-emerald-600 hover:bg-emerald-700 text-white rounded-lg font-semibold">Save About</button>
                <button onClick={()=>setAuthorForm(author || emptyAuthorForm)} className="px-4 py-2 border rounded-lg">Reset</button>
              </div>
            </div>
          </div>

          <div className="mt-6 pt-4 border-t border-dashed border-slate-300 dark:border-slate-700">
             <button onClick={() => logoutAdmin(navigate)} className="w-full px-5 py-2 bg-rose-600 hover:bg-rose-700 text-white rounded-lg font-semibold transition shadow-md">Logout Admin</button>
          </div>
        </aside>
      </div>
    </motion.main>
  );
}

// -----------------------
// Cart Drawer (responsive)
// -----------------------
function CartDrawer({cart, remove, onCheckout}){
  const total = cart.reduce((s,c)=>s+Number(c.price||0),0);
  const [open, setOpen] = useLocalState('ui_cart_open', false);
  return (
    <div>
      {/* Cart Toggle Button */}
      {cart.length > 0 && (
        <button onClick={()=>setOpen(true)} className="fixed right-6 bottom-6 z-50 px-5 py-3 bg-amber-500 hover:bg-amber-600 text-white rounded-full shadow-xl font-semibold transition transform hover:scale-105 focus:outline-none focus:ring-4 focus:ring-amber-500/50">
          Cart ({cart.length})
        </button>
      )}

      <AnimatePresence>
        {open && (
          <motion.aside
            initial={{x:'100%'}}
            animate={{x:0}}
            exit={{x:'100%'}}
            transition={{ type: 'tween', duration: 0.3 }}
            className="fixed right-0 top-0 h-full w-full md:w-96 bg-white dark:bg-slate-800 shadow-2xl z-50 flex flex-col"
          >
            <div className="p-6 flex items-center justify-between border-b dark:border-slate-700">
              <h4 className="font-serif text-xl">Your Shopping Cart</h4>
              <button onClick={()=>setOpen(false)} className="p-2 text-slate-500 hover:text-slate-900 dark:hover:text-slate-100 transition">
                <svg className="w-6 h-6" viewBox="0 0 24 24" fill="none" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" /></svg>
              </button>
            </div>
            <div className="p-4 overflow-y-auto flex-1">
              {cart.length===0 ? <div className="text-sm text-slate-500 p-4 text-center">No items in cart</div> : (
                cart.map((c,i)=> (
                  <div key={i} className="flex items-center gap-4 py-3 border-b border-dashed dark:border-slate-700">
                    <img src={c.images?.[0]} alt={c.title} className="w-12 h-18 object-cover rounded-md" onError={(e) => {e.target.onerror = null; e.target.src='https://placehold.co/100x150?text=Img';}} />
                    <div className="flex-1">
                      <div className="font-medium leading-snug">{c.title}</div>
                      <div className="text-xs text-slate-500">{c.subtitle}</div>
                    </div>
                    <div className="text-right">
                      <div className="font-semibold text-amber-600 dark:text-amber-500">{currency(c.price)}</div>
                      <button onClick={()=>remove(i)} className="text-rose-500 text-xs mt-1 hover:underline">Remove</button>
                    </div>
                  </div>
                ))
              )}
            </div>
            <div className="p-6 border-t dark:border-slate-700">
              <div className="flex items-center justify-between mb-4">
                <div className="text-lg font-medium">Subtotal</div>
                <div className="font-bold text-xl text-amber-600 dark:text-amber-500">{currency(total)}</div>
              </div>
              <button onClick={() => { setOpen(false); onCheckout(); }} disabled={cart.length === 0} className="w-full px-4 py-3 bg-slate-900 hover:bg-slate-700 disabled:bg-slate-400 text-white rounded-lg font-semibold transition shadow-md">
                Proceed to Checkout
              </button>
            </div>
          </motion.aside>
        )}
      </AnimatePresence>
    </div>
  );
}

// -----------------------
// App
// -----------------------
export default function App(){
  const [books, setBooks] = useLocalState('pt_books', SAMPLE_BOOKS);
  const [author, setAuthor] = useLocalState('pt_author', SAMPLE_AUTHOR);
  const [cart, setCart] = useLocalState('pt_cart', []);
  const [modal, setModal] = useState({ isOpen: false, title: '', message: '', onConfirm: null });
  const [isAdmin, setIsAdmin] = useLocalState('pt_is_admin', false);

  // Admin Credentials
  const ADMIN_ID = 'admin';
  const ADMIN_PASSWORD = 'secret123';

  // Central function to trigger the custom modal
  function showAlert(title, message, onConfirm = null) {
    setModal({ isOpen: true, title, message, onConfirm });
  }

  function addToCart(book){
    setCart(prev => [...prev, book]);
    showAlert('Item Added', `${book.title} has been added to your cart.`);
  }

  function removeFromCart(idx){ setCart(prev => prev.filter((_,i)=>i!==idx)); }

  function checkout(){
    // This is where you would start the actual payment flow
    showAlert('Checkout Demo', 'Implement Stripe/Razorpay checkout flow here.');
  }

  function loginAdmin(id, password) {
    if (id === ADMIN_ID && password === ADMIN_PASSWORD) {
      setIsAdmin(true);
      return true;
    }
    return false;
  }

  // MODIFIED: logoutAdmin now accepts a navigate function from the calling component
  function logoutAdmin(navigateCallback) {
    showAlert('Confirm Logout', 'Are you sure you want to log out of the Admin dashboard?', () => {
      setIsAdmin(false);
      navigateCallback('/'); // Call the passed navigate function
    });
  }

  // Wrapper for Admin route access control
  const AdminRoute = ({ element }) => (
    isAdmin ? element : <Navigate to="/admin/login" replace />
  );


  return (
    <Router>
      <div className="min-h-screen bg-slate-50 dark:bg-slate-900 text-slate-900 dark:text-slate-100 font-[Inter]">
        <Header author={author} isAdmin={isAdmin} />

        <AnimatePresence mode="wait">
          <Routes>
            {/* Home page now contains all content */}
            <Route path="/" element={<Home books={books} author={author} showAlert={showAlert} />} />
            <Route path="/books" element={<Books books={books} showAlert={showAlert} />} />
            {/* REMOVED: Dedicated Partners Page Route */}
            <Route path="/book/:id" element={<BookDetail books={books} addToCart={addToCart} showAlert={showAlert} />} />
            <Route path="/about" element={<About author={author} />} />
            <Route path="/contact" element={<Contact showAlert={showAlert} />} />

            {/* Admin Login Route */}
            <Route path="/admin/login" element={<AdminLogin loginAdmin={loginAdmin} showAlert={showAlert} isAdmin={isAdmin} />} />

            {/* Protected Admin Dashboard Route */}
            <Route path="/admin" element={
              <AdminRoute 
                element={<Admin books={books} setBooks={setBooks} showAlert={showAlert} logoutAdmin={logoutAdmin} />}
              />
            } />

            <Route path="*" element={<main className="max-w-4xl mx-auto px-6 py-12"><h2 className="font-serif text-3xl">404 - Page Not Found</h2><p className="mt-4">The page you were looking for doesn't exist.</p></main>} />
          </Routes>
        </AnimatePresence>

        <Footer author={author} />

        <CartDrawer cart={cart} remove={removeFromCart} onCheckout={checkout} />

        <Modal modal={modal} setModal={setModal} />
      </div>
    </Router>
  );
}
